import { APP_INITIALIZER, provideZoneChangeDetection } from '@angular/core';
import { provideRouter } from '@angular/router';
import { provideHttpClient, withInterceptorsFromDi } from '@angular/common/http';
import { KeycloakService } from 'keycloak-angular';
import { routes } from './app.routes';
import { environment } from '../environments/environment';
function initializeKeycloak(keycloak) {
  return () => keycloak.init({
    config: {
      url: environment.keycloak.url,
      realm: environment.keycloak.realm,
      clientId: environment.keycloak.clientId
    },
    initOptions: {
      onLoad: 'check-sso',
      checkLoginIframe: false,
      pkceMethod: 'S256'
    },
    shouldAddToken: () => false
  }).catch(error => {
    console.error('Keycloak initialization error:', error);
    console.warn('Make sure Keycloak is running at ' + environment.keycloak.url);
  });
}
export const appConfig = {
  providers: [provideZoneChangeDetection({
    eventCoalescing: true
  }), provideRouter(routes), provideHttpClient(withInterceptorsFromDi()), KeycloakService, {
    provide: APP_INITIALIZER,
    useFactory: initializeKeycloak,
    multi: true,
    deps: [KeycloakService]
  }]
};
